//component is a special kind of fxn in javascript return html code
//we only import and rendor app in index.js file rest are not
//import in App or other
//custom component follow pascal naming but built in follow camel casing
import "./ExpenseItem.css";
function ExpenseItem(props) {
  const month = props.date.toLocaleString("en-US", { month: "long" });
  const date = props.date.toLocaleString("en-US", { day: "2-digit" });
  const year = props.date.getFullYear();
  return (
    <div>
      <div className="expense-item ">

        <div>{date}</div>
        <div>{month}</div>
        <div>{year}</div>
      </div>
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price">${props.amount}</div>
      </div>
    </div>
  );
}
export default ExpenseItem;
