import React,{useState} from "react";
import './ExpenseForm.css';
const ExpenseForm=(props)=>{

    const [enteredTitle,setEnterTitle]=useState('');
    const [enteredDiscription,setEnterDiscription]=useState('');
    const [enteredDate,setEnterDate]=useState('');
    const titleChangeHandler=(event)=>{
        setEnterTitle(event.target.value);
    };
    const DiscriptionChangeHandler=(event)=>{
        setEnterDiscription(event.target.value);
    };
const submitHandler=(event)=>{
event.preventDefault();
const ExpenseData ={
title:enteredTitle,
Discription:enteredDiscription,
date:new Date(enteredDate),
};
//console.log(ExpenseData); instead of logging pass it and use it
props.onSaveExpenseData(ExpenseData);
setEnterDiscription('');
setEnterDate('');
setEnterTitle('');
};
return(
    <form onSubmit={submitHandler} >
        <div className="new-expense__controls">
        <div className="new-expense__control">
                <label>Title</label>
                <input type="text" onChange={titleChangeHandler} value={enteredTitle}/>
            </div>
            <div className="new-expense__control">
                <label>Discription</label>
                <textarea cols={39} rows={6} value={enteredDiscription} onChange={DiscriptionChangeHandler}/>
            </div>
        </div>
        <div className="new-expense__actions">
        <button type="submit">Add Note</button>
       </div>
    </form>
);
};
export default NoteForm;
